}

fun find_maximum_safe(numbers: [i32; 5]) -> i32 {
    let mut max = numbers[0];
    let mut i = 1;

    while i < 5 {
        if numbers[i] > max {
            max = numbers[i];
        }
        i = i + 1;
    }

    max
}

