fun calculate_sum(data: &Vec<i32>) -> i32 {
    let mut total = 0;
    let mut i = 0;
    while i < data.len() {
        total = total + data[i];
        i = i + 1;
    }
    total
}

fun calculate_average(data: &Vec<i32>) -> f64 {
    let sum = calculate_sum(data);
    (sum as f64) / (data.len() as f64)
}

fun find_maximum(data: &Vec<i32>) -> i32 {
    let mut max = data[0];
    let mut i = 1;
    while i < data.len() {
        if data[i] > max {
            max = data[i];
        }
        i = i + 1;
    }
    max
}

