fun integer_sqrt(n: i32) -> i32 {
    if n < 2 {
        return n;
    }

    let mut x = n / 2;
    let mut prev = 0;

    while x != prev {
        prev = x;
        x = (x + n / x) / 2;
    }

    x
}

fun gcd(mut a: i32, mut b: i32) -> i32 {
    while b != 0 {
        let temp = b;
        b = a % b;
        a = temp;
    }
    a
}

