// 1. Buffered I/O
fn process_file_naive(path) {
    let file = open(path)
    while !file.eof() {
        let byte = file.read_byte()  // Syscall for each byte
        process(byte)
    }
}

fn process_file_optimized(path) {
    let file = BufferedReader::new(open(path), 8192)
    while !file.eof() {
        let byte = file.read_byte()  // Reads from buffer
        process(byte)
    }
}

