class EventStore {
    let events = []
    let snapshots = {}
    let projections = {}

    fn append(event) {
        event.id = generate_id()
        event.timestamp = current_time()
        event.version = events.len() + 1

        events.push(event)
        update_projections(event)

        // Create snapshot every 100 events
