/// Calculates the factorial of a positive integer
/// 
/// # Examples
/// ```ruchy
/// let result = factorial(5);
/// assert_eq(result, 120);
/// ```
///
/// # Panics
/// Panics if n is negative
fun factorial(n: int) -> int {
    if n < 0 {
        panic("Factorial not defined for negative numbers");
    }

    if n == 0 || n == 1 {
        1
    } else {
        n * factorial(n - 1)
    }
}


