fn check_cpu() {
    let usage = system::cpu_usage() * 100
    if usage > checks.cpu_threshold {
        alerts.push({
            level: "WARNING",
            message: "CPU usage high: " + usage.to_s() + "%",
            time: current_datetime()
        })
        return false
    }
