// Circuit breaker pattern
struct CircuitBreaker {
    failure_count: i32
    last_failure: Option<Instant>
    state: State
}

impl CircuitBreaker {
    fn call<T>(mut self, f: Fn() -> Result<T, Error>) -> Result<T, Error> {
        match self.state {
            State::Open => {
                if self.should_attempt() {
                    self.state = State::HalfOpen
                } else {
                    return Err(Error::CircuitOpen)
                }
            }
            _ => {}
        }

        match f() {
            Ok(result) => {
                self.reset()
                Ok(result)
            }
            Err(e) => {
                self.record_failure()
                Err(e)
            }
        }
    }
}

