// Trait for adding context
trait Context<T> {
    fn context(self, msg: String) -> Result<T, Error>
    fn with_context<F>(self, f: F) -> Result<T, Error>
        where F: FnOnce() -> String
}

impl<T, E: Into<Error>> Context<T> for Result<T, E> {
    fn context(self, msg: String) -> Result<T, Error> {
        self.map_err(|e| {
            Error::new(e.into())
                .with_message(msg)
        })
    }

    fn with_context<F>(self, f: F) -> Result<T, Error>
        where F: FnOnce() -> String
    {
        self.map_err(|e| {
            Error::new(e.into())
                .with_message(f())
        })
    }
}

