fun find_max(numbers) {
    if numbers.len() == 0 {
        -999999  // Sentinel value for empty
    } else {
        let mut max_val = numbers[0]
        let mut i = 1
        while i < numbers.len() {
            if numbers[i] > max_val {
                max_val = numbers[i]
            }
            i = i + 1
        }
        max_val
    }
}

