// Parallel processing with channels
fn process_files(files: Vec<String>) -> Vec<Result> {
    let (sender, receiver) = channel()

    // Spawn worker for each file
    for file in files {
        let tx = sender.clone()
        spawn {
            let result = process_file(file)
            tx.send((file, result))
        }
    }

    // Collect results
    let results = Vec::new()
    for _ in files.len() {
        let (file, result) = receiver.recv()
        results.push(result)
    }

    return results
}

