fn process_with_progress(files) {
    let total = files.len()
    let mut i = 0

    while i < total {
        let file = files[i]
        let progress = ((i + 1) * 100) / total
        print("\rProcessing... [" + progress.to_s() + "%] " + file)

        // Do the actual work
        process_file(file)

        // Small delay to show progress
        sleep(100)  // 100ms
        i = i + 1
    }

    println("\n✅ All files processed!")
}

