        "3" => complete_task(tasks)
        "4" => {
            list_tasks(tasks)
            let index = input("Which task to delete? (number): ").to_i() - 1
            if index >= 0 && index < tasks.len() {
                tasks.remove(index)
                save_tasks(tasks)
                println("🗑️  Task deleted!")
            }
        }
        "5" => {
            println("👋 Goodbye!")
            break
        }
        _ => println("❌ Invalid choice")
    }
}

