fn draw_bar_chart(data, title) {
    println("\n" + title)
    println("=" * title.len())

    let max_value = data.values().max()
    let max_bar_width = 50

    for key, value in data.items() {
        let bar_width = (value * max_bar_width) / max_value
        let bar = "█" * bar_width.to_i()
        println(key + " │" + bar + " " + value.to_s())
    }
}

